INSERT INTO `bugs` (`id`, `module`, `status`, `priority`, `reporter`, `coder`, `subject`, `description`, `time`, `lastupdate`) VALUES (42, 21, 9, 6, 3, 0, 'Email the Trainees', 'It\\\'d be nice if we could email just the Trainees in our\r\nKabal, instead of the Kabal as a whole. It\\\'d be useful for\r\nsending communiques re: the NTC, Welcome mails, quick information, etc.', 1004564798, 20020916030629);
INSERT INTO `bugs` (`id`, `module`, `status`, `priority`, `reporter`, `coder`, `subject`, `description`, `time`, `lastupdate`) VALUES (43, 12, 7, 6, 3, 0, 'Lottery Semi-Mega-Pick', 'Maybe this should just be a note attached to NightWeaver\\\'s\r\nrequest, but hell, let\\\'s be egotistic and believe that my\r\nrequests are important enough to deserve their very own\r\nspace.\r\n\r\nAnyway... as you may or may not have noticed, I (almost)\r\nalways pick 4-2-1 as one of my tickets. Call them lucky\r\nnumbers if you want. Whatever. But now that I\\\'ve bought my\r\n4-2-1 ticket, I can no longer use the 10-SlikPiks feature.\r\n\r\nMaybe if you could modify it to auto-buy as many tickets as needed until the limit of 10 is reached. Or remove the 10-SlikPiks and instead have a field for # of SlikPiks... so I can buy say 4-2-1, 9-12-6, and then 8 random tickets.\r\n\r\n::shrug::', 1004565289, 20020601142159);
