INSERT INTO `bugs` (`id`, `module`, `status`, `priority`, `reporter`, `coder`, `subject`, `description`, `time`, `lastupdate`) VALUES (45, 9, 7, 6, 3, 666, 'Junkyard Organization', 'What w. the latest AWOL check and repossession of ships...\r\nthe Junkyard\\\'s rather crowded. Would it be too much to ask\r\nif the auctions could be separated between auctions run by\r\nindividual Hunters and auctions run by SSL Wreckers?\r\n\r\nI can think of many other ways to organize it... I bet you\r\ncould even set up a search/filter thingamajig (too lazy to\r\nremember the real term) so the plebeian Hunters and I can\r\ncan view auctions by price, by owner, by name, ascending or\r\ndescending... but hell, the dichotomy described above would\r\nprobably be a lot easier.', 1004806204, 20020522111303);
INSERT INTO `bugs` (`id`, `module`, `status`, `priority`, `reporter`, `coder`, `subject`, `description`, `time`, `lastupdate`) VALUES (46, 9, 7, 7, 94, 0, 'Adding Mods', 'For some reason mods which take up 0 inner mass, 0 outer mass\r\nand 0 cargo, sometimes trip the checks on available space even\r\nwhen there is space. Can\\\'t add em to a purchase list.\r\n\r\nCan\\\'t find what is causing this, think about rewriting it.', 1004850392, 20020209044325);
INSERT INTO `bugs` (`id`, `module`, `status`, `priority`, `reporter`, `coder`, `subject`, `description`, `time`, `lastupdate`) VALUES (47, 9, 7, 6, 1036, 0, 'Selling Ships', 'Yes, I know we can auction them, but I\\\'d like to see a couple other options for getting rid of ships: (1) Selling\r\nships: put in a link to be able to sell ships back to SSL\r\nfor something like 90% of the current price (including\r\nmods). (2) Giving a ship: Just put in a field that, with an\r\nID no. typed in it, and a button pressed, gives that ship\r\nas a gift to someone.', 1004975253, 20011105115645);
