INSERT INTO `bugs` (`id`, `module`, `status`, `priority`, `reporter`, `coder`, `subject`, `description`, `time`, `lastupdate`) VALUES (60, 8, 4, 6, 144, 666, 'New text for \\&quot;Welcome to the BHG\\&quot; Email', 'Hunter,\r\n\r\nCongratulations!  You have been accepted as a member\r\nof the Bounty Hunters Guild, the most prestigious\r\norganization of its kind in the galaxy.  In the Bounty\r\nHunters Guild you will participate in engaging\r\nactivities which will earn you credits and enable you\r\nto advance your standing in the BHG, learn and operate\r\nwithin our command structure, and socialize with other\r\nhunters, making fast friends in the process.\r\n\r\nIn order to familiarize yourself with this command\r\nstructure (which is essential, so that you know what\r\nto expect in the BHG), it is recommended that you\r\nthoroughly examine the BHG\\\'s website,\r\nhttp://www.thebhg.org/, especially the Manual portion,\r\nwhich will explain everything.  If you still have\r\nquestions, do not hesitate to contact your Kabal\r\nChief, or the Judicator.\r\n\r\nThe BHG is part of a larger organization known as the\r\nEmperor\\\'s Hammer (EH).  Founded in 1995, the EH is the\r\nlargest online Star Wars club on the Internet, and is\r\ncommanded by its founder, Grand Admiral Ronin.  BHG\r\nmembers are also loyal members of the EH and are\r\nencouraged to explore the other opportunities within\r\nthe Emperor\\\'s Hammer.  The EH website is\r\nhttp://www.emperorshammer.org./\r\n\r\nWelcome to the BHG!  You are now a member of (KABAL)\r\nKabal, whose chief is (CHIEF).  Your listed e-mail\r\naddress is (EMAIL).\r\n\r\nYour new ID line is (ID LINE).  Please use this ID\r\nline in all BHG communications.\r\n\r\nOnce again, please enjoy your time in the BHG and make\r\nthe most of the opportunities here.  If you\\\'re strong\r\nenough and skilled enough, you may rise in the ranks\r\nuntil you too sit on the Commission, surrounded by the\r\nBHG\\\'s all-time greats.\r\n\r\nShoot straight,\r\n\r\nUnderlord (UNDERLORD).\r\n', 1009094120, 20020523034049);
INSERT INTO `bugs` (`id`, `module`, `status`, `priority`, `reporter`, `coder`, `subject`, `description`, `time`, `lastupdate`) VALUES (61, 8, 4, 6, 144, 666, 'New text for \\&quot;You have been AWOLed\\&quot; Email', '\\&quot;TRN (Name),\r\n\r\nThis is to notify you of your transfer from (Kabal)\r\nKabal to the Unassigned Pool.\r\n\r\nPeriodically, Kabal Chiefs conduct AWOL checks.  If\r\nmembers do not respond to these checks within two\r\nweeks, they are transferred to the Unassigned Pool. \r\nOnce there, members can regain their spots in their\r\nold Kabals by contacting either their old Chiefs or\r\nthe Underlord.\r\n\r\nIf after two weeks they fail to do this, they are\r\nremoved from the roster.\r\n\r\nI hope that you will use this opportunity to resume\r\nactivity within the BHG.  Please let us know as soon\r\nas possible if you wish to do this.\r\n\r\nThe Office of the Underlord\\&quot;\r\n', 1009094198, 20020523034707);
