INSERT INTO `newsboard` (`topicid`, `timestamp`, `poster`, `topic`, `message`) VALUES (1, 1023959754, 666, 'New look', 'The bug tracker now has a new look. It\'s not very good, naturally, since I\'m a lousy graphics guy, but it\'s a bit different to the boring old black-on-white.');
INSERT INTO `newsboard` (`topicid`, `timestamp`, `poster`, `topic`, `message`) VALUES (2, 1027606088, 666, 'Statistics', 'Some boring statistics on who reports bug, gets assigned them, manages them, and a whole heap of other things is available <a href="index.php?page=stats">here</a>.');
INSERT INTO `newsboard` (`topicid`, `timestamp`, `poster`, `topic`, `message`) VALUES (3, 1028038627, 666, 'New major version', 'I\'ve upgraded the bug tracker to version 3.0.0, which has a number of juicy new features. The change log is as follows:\r<br>\r<br>1. Added my, search, and dosearch pages.\r<br>2. Changed page layout and formatting.\r<br>3. Added the ability to choose a bug owner from the complete list of coders for that module, rather than only being able to assign a bug to yourself.\r<br>4. Added a notification e-mail when a bug is assigned to you by someone else.\r<br>5. Added a notification e-mail when a bug you own has a note added to it.\r<br>6. Changed the menu.\r<br>7. Renamed the bug tracker codebase to "Roach Motel".');
INSERT INTO `newsboard` (`topicid`, `timestamp`, `poster`, `topic`, `message`) VALUES (4, 1028093917, 666, 'My Bugs page', 'I\'ve fixed a couple of problems with the My Bugs page, so it\'s actually displaying the correct bug lists now. Most coders will probably find more bugs on there now than there were before.');
