INSERT INTO `notes` (`id`, `bugid`, `writer`, `time`, `note`) VALUES (549, 257, 666, 1034182563, 'Got bored. Here\\\'s some untested and undocumented code that\r\nshould get us most of the way there:\r\n\r\nclass Exam extends BHGObject {\r\n	var $exam_id;\r\n	var $exam_person;\r\n	var $exam_name;\r\n	var $exam_score;\r\n	var $exam_is_graded;\r\n	var $exam_date_taken;\r\n	var $exam_passed;\r\n\r\n	function Exam($id, $coder = \\\'\\\') {\r\n		BHGObject::BHGObject($coder);\r\n		\r\n		$result = mysql_query(\\\'SELECT thebhg_ntc.exams.name, thebhg_ntc.exams.passing_grade, thebhg_ntc.exam_results.score, thebhg_ntc.exam_results.date_taken, thebhg_ntc.exam_results.bhg_id AS person, thebhg_ntc.exam_results.is_graded, thebhg_ntc.exam_results.date_taken FROM thebhg_ntc.exams, thebhg_ntc.exam_results WHERE thebhg_ntc.exams.id=thebhg_ntc.exam_results.exam AND thebhg_ntc.exam_results.id=\\\' . $id, $this-&gt;roster_db);\r\n\r\n		if ($result &amp;&amp; mysql_num_rows($result)) {\r\n			$row = mysql_fetch_array($result);\r\n			$this-&gt;exam_id = $id;\r\n			$this-&gt;exam_person = new Person($row[\\\'bhg_id\\\'], $coder);\r\n			$this-&gt;exam_name = stripslashes($row[\\\'name\\\']);\r\n			$this-&gt;exam_score = $row[\\\'score\\\'];\r\n			$this-&gt;exam_is_graded = $row[\\\'is_graded\\\'];\r\n			$this-&gt;exam_date_taken = $row[\\\'date_taken\\\'];\r\n			$this-&gt;exam_passed = ($row[\\\'passing_grade\\\'] &gt;= $row[\\\'score\\\']);\r\n		}\r\n		else {\r\n			$this-&gt;roster_error = \\\'Invalid exam ID. Could not create Exam object.\\\';\r\n		}\r\n	}\r\n\r\n	function GetPerson() {\r\n		return $this-&gt;exam_person;\r\n	}\r\n\r\n	function GetName() {\r\n		return $this-&gt;exam_name;\r\n	}\r\n\r\n	function GetScore() {\r\n		return $this-&gt;exam_score;\r\n	}\r\n\r\n	function GetDateTaken() {\r\n		return $this-&gt;exam_date_taken;\r\n	}\r\n\r\n	function IsGraded() {\r\n		return $this-&gt;exam_is_graded;\r\n	}\r\n\r\n	function Passed() {\r\n		return $this-&gt;exam_passed;\r\n	}\r\n\r\n}\r\n\r\nclass Person {\r\n\r\n	...\r\n	\r\n	function GetExams() {\r\n		$exams_result = mysql_query(\\\'SELECT thebhg_ntc.exam_results.id FROM thebhg_ntc.exam_results WHERE thebhg_ntc.exam_results.bhg_id=\\\' . $this-&gt;person_id, $this-&gt;roster_db);\r\n		$exams = array();\r\n		if ($exams_result &amp;&amp; mysql_num_rows($exams_result)) {\r\n			while ($exam = mysql_fetch_array($exams_result)) {\r\n				$exams[] = new Exam($exam[\\\'id\\\'], $this-&gt;roster_coder);\r\n			}\r\n		}\r\n		return $exams;\r\n	}\r\n\r\n}');
